            console.log('rangepicker: [%s, %s]', range[0] && range[0].format('YYYY-MM-DD'), range[1] && range[1].format('YYYY-MM-DD'));
        });
    }

    printError = (name) => {
        if (this.field.getError(name)) {
            return <span className="error-msg">{this.field.getError(name).join(',')}</span>;
        }
    }

    render() {
