class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            dataSource: dataSource(),
            filterMode: 'multiple'
        };
    }
    onSort(dataIndex, order) {
        const dataSource = this.state.dataSource.sort(function(a, b) {
            const result = a[dataIndex] - b[dataIndex];
            return  (order === 'asc') ? (result > 0 ? 1 : -1) : (result > 0 ? -1 : 1);
        });
        this.setState({
            dataSource,
            sort: {id: order}
        });
    }
    onFilter(filterParams) {
        let ds = dataSource();
        Object.keys(filterParams).forEach(key => {
            const selectedKeys = filterParams[key].selectedKeys;
            if (selectedKeys.length) {
                ds = ds.filter(record => {
                    return selectedKeys.some(value => {
                        return record[key].indexOf(value) > -1;
                    });
                });
            }
        });
        this.setState({dataSource: ds});
    }
    changeMode() {
        this.setState({
            filterMode: 'single'
        });
    }
    clearSort() {
        // your code to reset dataSource
        const newDataSource = this.state.dataSource;

        this.setState({
            dataSource: newDataSource,
            sort: {id: ''}
        });
    }
    render() {
        const filters = [{
            label: 'Nano 3',
            value: 3
        }, {
            label: 'Nano 678',
            value: 678,
            children: [{
                label: 'Nano 67',
                value: 67,
                children: [{
                    label: 'Nano 6',
                    value: 6
                }, {
                    label: 'Nano 7',
                    value: 7
                }]
