import { Upload, Button, Dialog } from '@alifd/next';
import Cropper from 'react-cropper';


function convertBase64UrlToFile(urlData) {

    const bytes = window.atob(urlData.split(',')[1]);

    const ab = new ArrayBuffer(bytes.length);
    const ia = new Uint8Array(ab);
    for (let i = 0; i < bytes.length; i++) {
        ia[i] = bytes.charCodeAt(i);
    }

    const blob = new Blob([ab], {type: 'image/png'});

    return new File([blob], 'test.png', {type: 'image/png'});
}

class App extends React.Component {
    constructor(props) {
        super(props);
        this.uploader = new Upload.Uploader({
            action: 'https://www.easy-mock.com/mock/5b713974309d0d7d107a74a3/alifd/upload',
            onSuccess: this.onSuccess,
            name: 'file'
        });
    }

    state = {
        src: null,
        visible: false,
        img: null,
    };

    onSuccess = (value) => {
        console.log(value);
        this.setState({
            img: value.url
        });
    };

    onSelect = (files) => {
        const reader = new FileReader();
        reader.onload = () => {
            this.setState({
                src: reader.result,
                visible: true,
            });
        };
        reader.readAsDataURL(files[0]);
    };

    onCancel = () => {
        this.setState({
            visible: false,
        });
    };

    onOk = () => {

        const data = this.cropperRef.getCroppedCanvas().toDataURL();

        const blob = convertBase64UrlToFile(data);
        const file = new File([blob], 'test.png', {type: 'image/png'});

        // start upload
        this.uploader.startUpload(file);

        this.setState({
            visible: false,
        });
    };

    saveCropperrRef = (ref) => {
        this.cropperRef = ref;
    };

    render() {
        return (
            <div>
